<style lang="scss">
@use "sass:map";

* {
  transition: fill 4s ease-out, color 4s ease-out, background-color 4s ease-out;
}

.content_wrapper {
  user-select: none;

  --color_dark: #{$color_dark};
  --color_medium: #{$color_medium};
  --color_light: #{$color_light};

  background-color: var(--color_light);
  color: var(--color_dark);

  @each $trait, $colors in $palettes {
    &.theme_#{$trait} {
      $dark: map.get($colors, dark);
      $medium: map.get($colors, medium);
      $light: map.get($colors, light);

      --color_dark: #{$dark};
      --color_medium: #{$medium};
      --color_light: #{$light};
    }
  }

  .splash {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100dvw;
    height: 100dvh;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 5;
    background-color: var(--color_light);

    .splash_logo {
      width: 40%;
      fill: var(--color_dark);
    }
  }

  .overlay {
    position: fixed;
    inset: 0;
    opacity: 0;
    pointer-events: none;
    transition: opacity .3s ease-in-out;
    z-index: 2;
    background-color: var(--color_dark);

    &.menu_open {
      opacity: .2;
      pointer-events: auto;
    }
  }

  .menu_toggle,
  .scroll_reset {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 3rem;
    height: 3rem;
    border-radius: 1.5rem;
    cursor: pointer;
    z-index: 4;
    background-color: var(--color_dark);
    color: var(--color_light);
    transition: background-color 4s ease-out, color 4s ease-out;

    .material-symbols-rounded {
      font-size: 2rem;
      font-weight: 450;
    }

    @media (hover: hover) {

      &:hover,
      &:active {
        background-color: var(--color_medium);
        transition: background-color 0s;
      }

      &:not(:hover) {
        transition: background-color 0s;
      }
    }
  }

  .menu_toggle {
    position: fixed;
    bottom: 0.5rem;
    right: 0.5rem;
  }

  .scroll_reset {
    position: fixed;
    bottom: .5rem;
    left: .5rem;
    pointer-events: none;
    transform: translateY(150%);
    transition: transform .3s ease-in-out, background-color 4s ease-out, color 4s ease-out;

    &.visible {
      transform: translateY(0);
      opacity: 1;
      pointer-events: auto;
    }
  }

  .nav {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    padding: .5rem .5rem 4rem .5rem;
    gap: .5rem;
    position: fixed;
    bottom: 0;
    right: 0;
    z-index: 3;
    transform: translateX(150%);
    pointer-events: none;
    visibility: hidden;
    transition: transform .3s ease-in-out;

    &.open {
      transform: translateX(0);
      pointer-events: auto;
      visibility: visible;
      transition: transform .3s ease-in-out;
    }

    .nav_list {
      display: flex;
      flex-direction: column;
      align-items: end;
      gap: .5rem;
      pointer-events: none;
      transform: translateX(150%);
      transition: all .3s ease-in-out;

      &.visible {
        pointer-events: auto;
        transform: translateX(0);
      }

      .nav_list_elem {
        display: flex;
        justify-content: end;
        width: fit-content;

        a {
          display: flex;
          align-items: center;

          .nav_list_elem_tag {
            display: flex;
            align-items: center;
            justify-content: center;
            height: 3rem;
            padding: 0 3.5rem 0 1rem;
            margin-right: -3rem;
            border-radius: 1.5rem;
            font-family: 'Nunito';
            font-size: clamp(1.25rem, 2.5dvw, 1.5rem);
            font-weight: 600;
            background-color: var(--color_light);
            border: 3px solid var(--color_dark);
            transition: background-color 0s, border 0s;
          }

          .material-symbols-rounded {
            display: flex;
            align-items: center;
            justify-content: center;
            width: 3rem;
            height: 3rem;
            border-radius: 1.5rem;
            font-size: 2rem;
            font-weight: 450;
            background-color: var(--color_light);
            border: 3px solid var(--color_dark);
            transition: background-color 0s, border 0s;
          }
        }

        @media (hover: hover) {
          &:hover {

            .nav_list_elem_tag,
            .material-symbols-rounded {
              background-color: var(--color_dark);
              color: var(--color_light);
              transition: background-color 0s, color 0s;

            }
          }
        }

        &.current {

          .nav_list_elem_tag,
          .material-symbols-rounded {
            background-color: var(--color_medium);
            border: none;
            color: var(--color_light);
          }
        }
      }
    }
  }

  .header {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100dvw;
    position: fixed;
    top: 0;
    font-size: clamp(2.5rem, 3dvw, 5rem);
    z-index: 1;

    .header_elements {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 100%;
      height: 6rem;
      padding-top: clamp(.5rem, 3dvw, 5rem);
      z-index: 3;
      background-color: var(--color_dark);

      a {
        display: flex;
        align-items: center;
        justify-content: center;

        .header_title {
          width: 100%;
          height: 100%;
          fill: var(--color_light);
        }
      }
    }

    .header_waves {
      position: relative;
      width: 100%;
      height: 6rem;
      margin-top: clamp(-8rem, -4dvw, -2rem);

      .wave {
        width: 100%;
        min-width: 640px;
        transform: scale(-1);
        pointer-events: none;

        &.wave_1 {
          position: absolute;
          top: 0;
          z-index: 2;
          fill: var(--color_dark);
        }

        &.wave_2 {
          position: absolute;
          top: .5rem;
          z-index: 1;
          fill: var(--color_medium);
        }
      }
    }
  }

  .main {
    display: flex;
    width: 100dvw;
    height: 100dvh;
    padding-top: 6rem;
  }
}

@media (max-width: 1024px) {
  .content_wrapper {
    .splash {
      .splash_logo {
        width: 60%;
      }
    }

    .header {
      .header_elements {
        a {
          .header_title {
            width: 90%;
          }
        }
      }
    }
  }
}

@media (max-width: 768px) {
  .content_wrapper {
    .splash {
      .splash_logo {
        width: 80%;
      }
    }

    .header {
      .header_elements {
        a {
          .header_title {
            width: 80%;
          }
        }
      }
    }
  }
}
